(function($)
{
    //http://stackoverflow.com/a/11777774/1469208
    $.extend($.fn,
    {
        fadeIn: function(ms)
        {
            if(typeof(ms) === 'undefined') ms = 500;

            $(this).css(
            {
                display: 'block',
                opacity: 0
            }).animate({
                opacity: 1
            }, ms);
            
            return this;
        },
        
        fadeOut: function(ms)
        {
            if(typeof(ms) === 'undefined') ms = 500;

            $(this).css(
            {
                opacity: 1
            }).animate({
                opacity: 0
            }, ms, 'linear', function()
            {
                $(this).css('display', 'none');
            });
            
            return this;
        }
    })
    })(Zepto)